mapper.select:
SELECT B.BL_DATE B_DATE, B.BL_ID BL_ID, P.PRI_NUM PRI_NUM, P.PRI_TITLE PRI_TITLE, I.PRIIMG_NAME PRIIMG_NAME, P.PRI_ADDR PRI_ADDR 
FROM PRIVATELOT P, BOOKLOT B, PRIVATEIMAGE I
WHERE P.PRI_NUM = B.PRI_NUM AND I.PRI_NUM = P.PRI_NUM AND B.U_ID = #{user}

SELECT COUNT(*) totalCount FROM (SELECT C.C_NUM C_NUM, I.CIMG_NAME CIMG_NAME, C.C_TYPE C_TYPE, C.C_TITLE C_TITLE, C.c_people c_people, C.C_PERIOD C_PERIOD, C.C_DATE C_DATE, C.C_PLACE C_PLACE, C.C_ENTRY C_ENTRY, C.C_HITS C_HITS FROM CLUB C JOIN CLUBIMAGE I ON c.c_num = i.c_num
C.C_TYPE like #{keyWord}
C.C_PLACE like #{one} ||'%'
)
