mapper.select:
select u_id, ps_star, ps_content, pri_num, ps_regdate
from postscript2 
where pri_num = #{pri_num}

SELECT t_num, pri_num, t_date, t_time
FROM timetable
WHERE t_date = #{t_date} AND pri_num = #{pri_num}

mapper.update:
UPDATE TIMETABLE
SET T_DATE = #{t_date}, T_TIME = #{t_time}
WHERE T_NUM = #{t_num}
